INSERT INTO `readbookinfos` VALUES ('https://www.xiaohongshu.com/discovery/item/62627a3100000000210385c0', '太古里附近新店，简约美式风格小店坐在路边吃个热狗，配瓶精酿超舒服?-✔️招牌恶霸⽜肉热狗9.9r蒜香黄油面包现烤的，中间是热狗肠铺了满满一层铁板牛肉片搭配烤肉酱，咬下去真的爆汁‼️✔️麻辣小龙虾热狗剥好的小龙虾覆盖了整个热狗麻麻辣辣的，很符合成都口味?还有芝士牛肉和牛肋条的也不错-搭配薯条的话可以选芝士薯条瀑布般的芝士倒下来很治愈～㊙️堂食比较划得来的是可乐雪碧可以无限续～#成都美食 #成都攻略 #美式热狗', 'https://sns-avatar-qc.xhscdn.com/avatar/6054a158b3e438b562520671.jpg?imageView2/1/w/540/format/jpg', '草莓大福go', '发布于 2022-04-22 17:49', '526', '10', '#成都美食,#成都攻略,#美式热狗', '新疆', '四川美食', '281', '//ci.xiaohongshu.com/e6004e3d-0338-d2bb-46f4-6f1434966d76?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/8bdf3fe9-2af7-cbfa-1b9d-948c98fa8e26?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/cdc9838a-1614-766a-125d-ed15523c97c2?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/56d47548-8cbe-1ecf-d9ea-a591b43e815a?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/6df27dcb-a9dc-3fb4-2332-c7002654c7eb?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/a8240ba9-a3f6-ce7f-61a0-e67b2522530f?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/b7401198-70c1-3624-76b5-9d746a20b314?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/7bbafb8d-5afd-a921-3e3f-1271a177103d?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/cba89f96-73a7-75a4-5145-fab7b22565ae?imageView2/2/w/1080/format/jpg', '0', '[{\"commentAvatar\": \"https://sns-avatar-qc.xhscdn.com/avatar/5f281ae20000000001001eaa.jpg?imageView2/2/w/80/format/jpg\", \"commentUserName\": \"enigma\\u265a\", \"commentTime\": \"04-25\", \"commentContent\": \"\\u5e97\\u540d\\u662f\\u5565\\u5440\"}, {\"commentAvatar\": \"https://sns-avatar-qc.xhscdn.com/avatar/602cc0933c8fa5e046b551fd.jpg?imageView2/2/w/80/format/jpg\", \"commentUserName\": \"\\u98de\\u884c\\u7684\\u8717\\u725b\", \"commentTime\": \"05-10\", \"commentContent\": \"\\u6c42\\u4ef7\\u4f4d\\u3001\\u516b\\u7ebf\\u5c0f\\u53bf\\u57ce\\u53ef\\u4ee5\\u62e5\\u6709\\u4e0d\\uff1f\"}, {\"commentAvatar\": \"https://sns-avatar-qc.xhscdn.com/avatar/5e65c89b000000000100b7