INSERT INTO `readbookinfos` VALUES ('https://www.xiaohongshu.com/discovery/item/624ea2d1000000000102b138', '没想到成都也可以吃到开口笑饺子！！再也不用跑去都江堰吃这口了！！一家开了几十年的一家老店 ?㊙️就在藏在桐梓林老小区的楼下的苍蝇馆子“开口笑饺子”是招牌 ?馅都在外面 居然煮不烂 味道是甜辣口 红油简直香掉头！外皮也很有较劲 7r?一碗真的巨巨巨良心！！▪️店名：汪记开口金水饺▫️地址：长寿南路70号成都美食 ｜小吃｜ 美食日常 ｜成都吃喝玩乐攻略 ｜春熙路美食 ｜成都探店 ｜成都旅游攻略 ｜成都小吃 ｜小红书爆款美食｜成都特色小吃 ｜水饺 ｜开口笑', 'https://sns-avatar-qc.xhscdn.com/avatar/5ca454eaa7e456000114bf4b.jpg?imageView2/1/w/540/format/jpg', 'kiko小辣椒', '发布于 2022-04-07 16:37', '3199', '10', '', '浙江', '四川美食', '301', '//ci.xiaohongshu.com/b31a996b-10f4-ae60-b9cc-fcf8d6f67d14?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/1290a26b-45dd-c6b9-1388-21a38c85a89f?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/a1f755e2-8201-db52-9281-d560fb847199?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/2d922eb4-715e-a765-6fb4-38c426fc8303?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/d001e132-e603-3292-484d-6c16eba54f92?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/12bc008e-f0f5-1aeb-ea00-46ad713d0aa4?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/5163682f-35eb-68d0-ffea-ccc9ce587aa2?imageView2/2/w/1080/format/jpg', '0', '[{\"commentAvatar\": \"https://sns-avatar-qc.xhscdn.com/avatar/6241948b2e44dc368cb5afb5.jpg?imageView2/2/w/80/format/jpg\", \"commentUserName\": \"\\u6843\\u6843\\u4e4c\\u9f99\", \"commentTime\": \"04-10\", \"commentContent\": \"\\u6211\\u6709\\u4e00\\u4e2a\\u95ee\\u9898 \\u8fd9\\u6837\\u716e\\u9985\\u771f\\u7684\\u4e0d\\u4f1a\\u6f0f\\u51fa\\u6765\\u5417\"}, {\"commentAvatar\": \"https://sns-avatar-qc.xhscdn.com/avatar/60336a71e3e2c42eadba8ac6.jpg?imageView2/2/w/80/format/jpg\", \"commentUserName\": \"\\u9633\\u9633\\u9633\\u9633\\u9633\\u9633\\u9633\", \"commentTime\": \"04-08\", \"commentContent\": \"\\u5929\\u5450\\uff01\\uff01\\uff01\\u5389\\u5bb3\\ud83d\\udc4d\\ud83c\\udffb\"}, {\"commentAvatar\": \"https://sns-avatar-qc.xhscdn.com/avatar/5cf9093e000000001800f762.jpg?imageView2/2/w/80/format/jpg\", \"commentUserName\": \"BBBYW\\ud83c\\ud