INSERT INTO `readbookinfos` VALUES ('https://www.xiaohongshu.com/discovery/item/5d936efe000000000401beec', '湖南美食太多太多…这一趟真的是来对了地～【地道美食不容错过】：长沙臭豆腐、剁椒鱼头、辣椒炒肉、剁椒炒蛋、黄鸭叫、皮蛋辣椒擂茄子、酸萝卜牛百叶、鸭掌筋、张家界三下锅、凤凰血粑鸭、石锅鱼…叫“口味…”的菜，都好好吃，口味虾、口味鱼、口味蛙、口味鸡杂、口味花菜…【去长沙必吃店铺】：- 文和友老长沙龙虾馆- 冬瓜山肉肠- 坛宗剁椒鱼头- 正宗桂林特色卤肉粉- 费大厨辣椒炒肉（辣椒炒肉）- 肆姐面粉馆- 津市刘聋子粉馆- 壹盏灯（鸭掌筋）- 炊烟时代小炒黄牛肉- 黑色经典臭豆腐- 茶颜悦色（幽兰拿铁/桂花弄）- 糖油坨坨', 'https://sns-avatar-qc.xhscdn.com/avatar/5c5d5fb3772b060001cb2d17.jpg?imageView2/1/w/540/format/jpg', 'ZoeC', '发布于 2019-10-01 23:21', '7943', '10', '', '香港', '湖南美食', '441', '//ci.xiaohongshu.com/9f06e4ef-b686-5c5a-9e7c-e58e44dcb968?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/7ae8272f-a664-5397-9778-b763ca3e5fdf?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/8b6c386c-b6e1-5b44-91e6-e298aec8e82c?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/33456575-177a-5a69-a096-d00a7271314a?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/2dc0626b-d57b-504f-98d1-e4014a4946ec?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/df0ef426-935a-5fb9-8543-ada6de56dd21?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/42e755f0-2b84-5e0a-a976-679f1602a803?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/36e5dced-7a82-5d2b-bd65-91865fecb903?imageView2/2/w/1080/format/jpg,//ci.xiaohongshu.com/da0a6699-60fa-527b-854f-e304d00f28c3?imageView2/2/w/1080/format/jpg', '0', '[{\"commentAvatar\": \"https://sns-avatar-qc.xhscdn.com/avatar/62bafefd1fd36ac160d43edf.jpg?imageView2/2/w/80/format/jpg\", \"commentUserName\": \"Tom\", \"commentTime\": \"06-27\", \"commentContent\": \"@\\u65e0\\u654c\\u7535\\u5b50\\u7f8a\"}, {\"commentAvatar\": \"https://sns-avatar-qc.xhscdn.com/avatar/625ae2af216d7a9bbffa5a6b.jpg?imageView2/2/w/80/format/jpg\", \"commentUserName\": \"Jessie\\uff01\", \"commentTime\": \"03-14\", \"commentContent\": \"@\\ud83c\\udf30\"}, {\"commentAvatar\": \"https://sns-avatar-qc.xhscdn.com/avatar/61c3206565092525a4d54a5b.jpg?imageView2/2/w/80/format/jpg\", \"commentUserName\": \"H.\", \"commentTime\": \